allow:
Bash(sqlite3 ~/.claude-task-tracker/tasks.db "
.mode column
.headers on

echo ''=== SESSIONS ==='';
SELECT session_id, project, substr\(original_goal, 1, 50\) as goal, current_status, last_activity FROM sessions ORDER BY last_activity DESC LIMIT 10;

echo '''';
echo ''=== PROGRESS ==='';
SELECT session_id, completed_count, total_count, updated_at FROM progress ORDER BY updated_at DESC LIMIT 10;

echo '''';
echo ''=== SNAPSHOTS ==='';
SELECT session_id, substr\(last_user_message, 1, 40\) as last_user, created_at FROM snapshots ORDER BY created_at DESC LIMIT 10;

echo '''';
echo ''=== TIMELINE \(最近20条\) ==='';
SELECT session_id, event_type, substr\(content, 1, 30\) as content, timestamp FROM timeline ORDER BY timestamp DESC LIMIT 20;
")
